import logo from "./logo.svg";
import "./App.css";
import { Component } from "react";

class App extends Component {
  constructor() {
    super(); /* Calls constructor of class you are extending i.e Component*/
    this.state = {
      name: "Yihua",
      color: "Yellow",
    };
  }

  render() {
    return (
      <div className="App">
        <header className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <p>Hi {this.state.name}</p>{" "}
          {/* We use brackets to Assess javascript variables */}
          <button
            onClick={() => {
              this.setState({ name: "New Person" });
              console.log(this.state);
            }}
          >
            Change Name
          </button>
        </header>
      </div>
    );
  }
}

export default App;
